import React, {useState} from 'react';
import './Home.css';
import {useNavigate} from 'react-router-dom';

function Home(){ 

    const navigate = useNavigate();
    const upperMbti = useState(["E","S","T","J"]);
    const lowerMbti = useState(["I","N","F","P"]);
    const meanMbti = useState(["energy",'recognition','decision','lifestyle']);
    const [select, setSelect]= useState(false); //버튼 중복 안되게

    const handleClick = (i)=> {
      const copy = Array(upperMbti.length).fill(false);
      copy[i] = true;
      setSelect(copy);
    }

    return (
      <div className="Home">
        <div className="upperTitle">
          <p>MBTI</p>
          <button onClick={()=>navigate('/admin')}>
            관리자 페이지</button>
        </div>

        <div className="chooseMbti">
          {
            upperMbti.map(function(a,i){
              return(
                <div className='upperMbti'>
                     <button 
                     className={select == meanMbti[i] ? 'select' : ""}
                     value={a} 
                     onClick={handleClick(i)}>{a}</button>
                </div>
              )
            })
          }
          <div className="meanMbti">
            <p>에너지 방향</p>
            <p>인식 방식</p>
            <p>판단</p>
            <p>생활 양식</p>
          </div>

          {
            lowerMbti.map(function(a,i){
              return(
                <div className='lowerMbti'>
                     <button 
                     className={`${select === meanMbti[i] ? 'select' : ''}`} 
                     value={a} 
                     onClick={handleClick(i)}>{a}</button>
                </div>
              )
            })
          }
           <div className="completeBtn">
              <input type='submit' value={"확인"} onClick={()=>{navigate('/result');}}/>
           </div>
        </div>

        <div className="lowerTitle"></div>
      </div>
    );
};

export default Home;
