import React, {useState} from 'react';
import './Home.css';
import {useNavigate} from 'react-router-dom';

function ChangeBtnColor(props){
  return(
    <div>
      <button>props.mean</button>
       props.mean[props.i]==0 ? : null  
    </div>
  )
}

function Home(){ 

    const navigate = useNavigate();
    const upperMbti = useState(["E","S","T","J"]);
    const lowerMbti = useState(["I","N","F","P"]);
    let [mean, clickedMean] = useState([0,0,0,0]); //버튼 중복 선택 안되게
    let [select, setSelect]= useState(0); //버튼 클릭시 색깔 바꿈
    let index = useState(0);

    return (
      <div className="Home">
        <div className="upperTitle">
          <p>MBTI</p>
          <button onClick={()=>navigate('/admin')}>
            관리자 페이지</button>
        </div>

        <div className="chooseMbti">
          {
            upperMbti.map(function(a,i){
              return(
                <div className='upperMbti'>
                     <button onClick={()=>{
                        setSelect(!select); 
                        clickedMean(!mean[i]);
                        index(i);
                     }}>{a}</button>
                </div>
              )
            })
          }
          {
            select==1?<ChangeBtnColor upperMbti={upperMbti} lowerMbti={lowerMbti} mean={mean} i={index}/> : null
          }
          <div className="meanMbti">
            <p>에너지 방향</p>
            <p>인식 방식</p>
            <p>판단</p>
            <p>생활 양식</p>
          </div>

          {
            lowerMbti.map(function(a,i){
              return(
                <div className='lowerMbti'>
                     <button 
                     onClick={handleClick(i)}>{a}</button>
                </div>
              )
            })
          }
           <div className="completeBtn">
              <input type='submit' value={"확인"} onClick={()=>{navigate('/result');}}/>
           </div>
        </div>

        <div className="lowerTitle"></div>
      </div>
    );
};

export default Home;
