import React, {useState} from 'react';
import './Home.css';
import {useNavigate} from 'react-router-dom';

function Button(props){
  const handleClick =()=>{
    onClick(props.i);
  };
  const btnStyle = {
    backgroundColor: props.select?'rgb(238, 127, 53' : 'black'
  }
  return(
    <button style={btnStyle}
     onClick={handleClick}>
     {props.mbti}</button>
  )
}

function Home(){ 

    const navigate = useNavigate();
    const upperMbti= ["E","S","T","J"];
    const lowerMbti = ["I","N","F","P"];
    let [select, setSelect]= useState([0,0,0,0]); //upper 클릭관리
    let [select2, setSelect2]= useState([0,0,0,0]); //lower 클릭관리


    const handleClick = (i)=>{
      if(select[i]==0&&select2[i]==0){
        const copy = [...select];
        copy[i]= 1;
        setSelect(copy);
        const copy2 = [...select2];
        copy2[i]=1;
        setSelect2(copy);
      }
    }

    return (
      <div className="Home">
        <div className="upperTitle">
          <p>MBTI</p>
          <button onClick={()=>navigate('/admin')}>
            관리자 페이지</button>
        </div>

        <div className="chooseMbti">
          {
            upperMbti.map(function(a,i){
              return(
                <div className='upperMbti'>
                     <Button onClick={()=>{handleClick}}
                         i={i}
                         mbti={a}
                         select={select[i]}
                     />

                </div>
              )
            })
          }
          <div className="meanMbti">
            <p>에너지 방향</p>
            <p>인식 방식</p>
            <p>판단</p>
            <p>생활 양식</p>
          </div>

          {
            lowerMbti.map(function(a,i){
              return(
                <div className='lowerMbti'>
                     <button>{a}</button>
                </div>
              )
            })
          }
           <div className="completeBtn">
              <input type='submit' value={"확인"} onClick={()=>{navigate('/result');}}/>
           </div>
        </div>

        <div className="lowerTitle"></div>
      </div>
    );
};

export default Home;
